mapper.select:
select
file_id,
file_name, file_size, file_ext, file_user_id, file_uuid, file_path,
file_state,
file_class_id, file_create_time,file_brief
from
file_info
where 
file_user_id = #{userId}  and file_id like #{searchInfo} 
or file_user_id = #{userId} and file_name like #{searchInfo} 
or file_state = 5  and file_id like #{searchInfo} 
or file_state = 5 and file_name like #{searchInfo}

mapper.update:
update file_info
set
file_state = #{fileState}
where
file_uuid = #{fileUuid}
